mapper.insert:
insert into tb_dzspring_goods (
		id
		, category
		, title
		, author
		, content
		, publisher
		, price
		, publishedDate
		, totalPage
		, goodsStatus
		, intro
		, authorIntro
		, publisherComment
		, recommendation
		, createdAt
	) values (
		#{id}
		, #{category}
		, #{title}
		, #{author}
		, #{content}
		, #{publisher}
		, #{price}
		, #{publishedDate}
		, #{totalPage}
		, #{goodsStatus}
		, #{intro}
		, #{authorIntro}
		, #{publisherComment}
		, #{recommendation}
		, #{createdAt}			
	)

mapper.update:
update tb_dzspring_goods set
	category = #{category}
	, title = #{title}
	, author = #{title}
	, content = #{content}
	, publisher = #{publisher}
	, price = #{price}
	, publishedDate = #{publishedDate}
	, totalPage = #{totalPage}
	, goodsStatus = #{goodsStatus}
	, intro = #{intro}
	, authorIntro = #{authorIntro}
	, publisherComment = #{publisherComment}
	, recommendation = #{recommendation}			
where id = #{id}
