	, quantity
	, receiverName
	, receivetPhone
	, payMethod	
) values (
	#{memberId}
	, #{goodsId}
	, #{quantity}
	, #{receiverName}
	, #{receiverPhone}
	, #{payMethod}
)

mapper.update:
update tb_dzspring_order set
memberId = #{memberId}
, goodsId = #{goodsId}
, quantity = #{quantity}
, receiverName = #{receiverName}
, receiverPhone = #{receiverPhone}
, payMethod = #{payMethod}
where id = #{id}

mapper.select:
select * from tb_dzspring_order where memberId = #{memberId}

	and id > #{lastId}

limit 10;
