mapper.insert:
insert into tb_dzspring_order (
	memberId
	, receiverName
	, receiverPhone
	, address
	, payMethod
) values (
	#{memberId}
	, #{receiverName}
	, #{receiverPhone}
	, #{address}
	, #{payMethod}
)

mapper.update:
update tb_dzspring_order set
memberId = #{memberId}
, receiverName = #{receiverName}
, receiverPhone = #{receiverPhone}
, address = #{address}
, payMethod = #{payMethod}

mapper.select:
select * from tb_dzspring_order_item where memberId = #{memberId}

	and id > #{lastId}

limit 10;

select * from tb_dzspring_order where 1=1
and id < #{lastId}
order by id desc limit 10

select * from tb_dzspring_order where 1=1
	and memberId = #{memberId}


		and id < #{lastId}


order by id desc
limit 10
